import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { Container, Card } from 'react-bootstrap';
import '../assets/styles/RadnyDetails.css';

const RadnyDetails = () => {
  const { id } = useParams();
  const [radny, setRadny] = useState(null);

  useEffect(() => {
    const fetchRadny = async () => {
      const { data, error } = await supabase
        .from('radni')
        .select('*')
        .eq('id', id)
        .single();

      if (error) {
        console.error('Error fetching radny:', error);
      } else {
        setRadny(data);
      }
    };

    fetchRadny();
  }, [id]);

  if (!radny) {
    return <div>Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <Card>
        <Card.Img variant="top" src={radny.photo_url} alt={`${radny.first_name} ${radny.last_name}`} />
        <Card.Body>
          <Card.Title>{radny.first_name} {radny.last_name}</Card.Title>
          <Card.Text>{radny.description}</Card.Text>
          <Card.Text><strong>Stanowisko:</strong> {radny.position}</Card.Text>
          <Card.Text><strong>Partia:</strong> {radny.party}</Card.Text>
          <Card.Text><strong>Email:</strong> <a href={`mailto:${radny.email}`}>{radny.email}</a></Card.Text>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default RadnyDetails;
